import { useMemo, useState } from "react";
import { useWorkoutsContext } from "../hooks/useWorkoutsContext";
import { useAuthContext } from "../hooks/useAuthContext";
import { DropDownList } from "@progress/kendo-react-dropdowns";

const WorkoutForm = () => {
  const { dispatch } = useWorkoutsContext();
  const { user } = useAuthContext();

  const [title, setTitle] = useState("");
  const [load, setLoad] = useState("");
  const [reps, setReps] = useState("");
  // const [plan, setPlan] = useState([25, 29, 35, 40]);
  const [plan, setPlan] = ["25", "29", "35", "40"];
  const data = [
    {
      id: 1,
      label: "Gold",
      type: "25",
    },
    {
      id: 2,
      label: "platinum w/ rainx",
      type: "29",
    },
    {
      id: 3,
      label: "elite",
      type: "35",
    },
    {
      id: 4,
      label: "elite w/ ceramic",
      type: "40",
    },
    {
      id: 5,
      label: "best package includes everything",
      type: "40",
    },
  ];
  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);

  // const filteredData = useMemo(() => {
  //   if (!plan || plan === "all") return data;

  //   return data.filter((item) => item.type === plan);
  // }, [plan]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      setError("You must be logged in");
      return;
    }

    const workout = { title, load, reps, plan };

    const response = await fetch("/api/workouts", {
      method: "POST",
      body: JSON.stringify(workout),
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${user.token}`,
      },
    });
    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
      setEmptyFields(json.emptyFields);
    }
    if (response.ok) {
      setTitle("");
      setLoad("");
      setReps("");
      setPlan("");
      setError(null);
      setEmptyFields([]);
      console.log("new workout added", json);
      dispatch({ type: "CREATE_WORKOUT", payload: json });
    }
  };

  return (
    <>
      <form className="create" onSubmit={handleSubmit}>
        <h3>Add a New Workout</h3>
        <label>Excersize Title:</label>
        <input
          type="text"
          onChange={(e) => setTitle(e.target.value)}
          value={title}
          className={emptyFields.includes("title") ? "error" : ""}
        />
        <label>Load (in kg):</label>
        <input
          type="number"
          onChange={(e) => setLoad(e.target.value)}
          value={load}
          className={emptyFields.includes("load") ? "error" : ""}
        />
        <label>Reps:</label>
        <input
          type="number"
          onChange={(e) => setReps(e.target.value)}
          value={reps}
          className={emptyFields.includes("reps") ? "error" : ""}
        />
        <label>Plan: </label>
        <input
          // type="text"
          onChange={(e) => setPlan(e.target.value)}
          value={plan}
          className={emptyFields.includes("plan") ? "error" : ""}
        />
        Pick your favorite flavor:
        <option value="grapefruit">Grapefruit</option>
        <option value="lime">Lime</option>
        <option value="coconut">Coconut</option>
        <option value="mango">Mango</option>
        {/* <input type="submit" value="Submit" /> */}
        <button>Add Workout</button>
        {error && <div className="error">{error}</div>}
      </form>
    </>
  );
};

export default WorkoutForm;

