import { useMemo, useState } from "react";
import { useWorkoutsContext } from "../hooks/useWorkoutsContext";
import { useAuthContext } from "../hooks/useAuthContext";
import { DropDownList } from "@progress/kendo-react-dropdowns";

const WorkoutForm = () => {
  const { dispatch } = useWorkoutsContext();
  const { user } = useAuthContext();

  const [title, setTitle] = useState("");
  const [load, setLoad] = useState("");
  const [reps, setReps] = useState("");
  const [plan, setPlan] = useState([25, 29, 35, 40]);
  const [error, setError] = useState(null);
  const [emptyFields, setEmptyFields] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!user) {
      setError("You must be logged in");
      return;
    }

    const workout = { title, load, reps, plan };

    const response = await fetch("/api/workouts", {
      method: "POST",
      body: JSON.stringify(workout),
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${user.token}`,
      },
    });
    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
      setEmptyFields(json.emptyFields);
    }
    if (response.ok) {
      setTitle("");
      setLoad("");
      setReps("");
      setPlan([25, 29, 35, 40]);
      setError(null);
      setEmptyFields([]);
      console.log("new workout added", json);
      dispatch({ type: "CREATE_WORKOUT", payload: json });
    }
  };

  return (
    <form className="create" onSubmit={handleSubmit}>
      <h3>Add a New Workout</h3>
      <label>Excersize Title:</label>
      <input
        type="text"
        onChange={(e) => setTitle(e.target.value)}
        value={title}
        className={emptyFields.includes("title") ? "error" : ""}
      />
      <label>Load (in kg):</label>
      <input
        type="number"
        onChange={(e) => setLoad(e.target.value)}
        value={load}
        className={emptyFields.includes("load") ? "error" : ""}
      />
      <label>Reps:</label>
      <input
        type="number"
        onChange={(e) => setReps(e.target.value)}
        value={reps}
        className={emptyFields.includes("reps") ? "error" : ""}
      />
      <label>Plan: </label>
      <input
        // type="text"
        onChange={(e) => setPlan(e.target.value)}
        value={plan}
        className={emptyFields.includes("plan") ? "error" : ""}
      />
      Pick your favorite flavor:
      <option value="grapefruit">Grapefruit</option>
      <option value="lime">Lime</option>
      <option value="coconut">Coconut</option>
      <option value="mango">Mango</option>
      {/* <input type="submit" value="Submit" /> */}
      <button>Add Workout</button>
      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default WorkoutForm;

// Dropdown categories
const categories = ["all", "recipe", "video", "article"];

// Results data filtered using categories
const data = [
  {
    id: 1,
    label: "Best Ramen ever",
    type: "recipe",
  },
  {
    id: 2,
    label: "Top 10 Mexican dishes",
    type: "article",
  },
  {
    id: 3,
    label: "How to prepare a whole roast chicken",
    type: "video",
  },
  {
    id: 4,
    label: "Chilli Chicken Gnocchi",
    type: "recipe",
  },
  {
    id: 5,
    label: "Best 5 ice desserts for hot summer",
    type: "article",
  },
];

export const RecipeDropDownList = () => {
  // Store currently selected category
  const [category, setCategory] = useState("");

  // Memoized results. Will re-evaluate any time selected
  // category changes
  const filteredData = useMemo(() => {
    if (!category || category === "all") return data;

    return data.filter((item) => item.type === category);
  }, [category]);

  return (
    <section className="k-my-8">
      <form className="k-form k-mb-4">
        <label className="k-label k-mb-3">Category</label>
        <DropDownList
          data={categories}
          onChange={(e) => setCategory(e.value)}
        />
      </form>

      <section className="k-listgroup">
        <ul>
          {filteredData.map((item) => {
            return (
              <li key={item.id} className="k-listgroup-item">
                {item.label}
              </li>
            );
          })}
        </ul>
      </section>
    </section>
  );
};
